/*

	Seedling module's purpose is to mediate connections to seeders data,
	which is split between a mongoDB store and a MYSQL Instance
	
	Seedling will manage the addition and authentication of users
	
*/


var seedling = exports;
var mysql = require('mysql');
var crypto = require('crypto')
var db;
var hashkey = 'e2580841-7a3a-4843-a711-bfd0ceffd59e';
var hash;



var connection = mysql.createConnection({
  host     : '127.0.0.1',
  user     : 'root',
  password : '',
  database: "seederDATA"
});



/**
 * Get an array of graph data from the public graphs database
 * @param      {String}   a string specifying which graphs to return (all, popular, new)
 * @param      {Function} callback will be passed a single argument which contains all graph metadata
 */
seedling.getPublic = function(filter, next) {

    if (filter == 'all') {
    
    //query returns 
    
    var query = connection.query('SELECT graphid,coverimage,graphname,description,nodecount,edgecount,likes,views,DATE_FORMAT(graphspublic.created,\'%d %b %y\') AS created,username,firstname,lastname,avatarurl FROM (SELECT * FROM graphs where graphs.public = 1) AS graphspublic LEFT JOIN users on (graphspublic.userid = users.id);', function(err, results) {
    	next(results);
    	});
   
    }

}

seedling.getUserGraphs = function(graphs_for_user, next){

var query = connection.query('SELECT graphid,coverimage,graphname,description,nodecount,edgecount,likes,views,DATE_FORMAT(graphs.created,\'%d %b %y\') AS created,username,firstname,lastname,avatarurl from graphs, (SELECT * from users WHERE username = \'' + graphs_for_user + '\') AS theuser where (graphs.userid = theuser.id);' ,function(err, results) {
	//console.log(err,results);
	if(err)
	{
		next(null);
	}else{
		next(results);
	}
	
	});


}

seedling.getUser = function(requested_username, next){

	//if user found return user object, else return a null (site will render 404)
	
	var query = connection.query('SELECT firstname,lastname,username,avatarurl,bio,locationid,bannerurl from users WHERE username = \'' + requested_username +'\';',function(err, results) {
		//console.log(err,results);
		if(err)
		{
			next(null);
		}else{
			next(results);
		}
		
		});

}

seedling.updateGraphViews = function(idtoupdate){
	
	var query = connection.query('UPDATE graphs SET views=views+1 WHERE graphid = \'' + idtoupdate +'\';',function(err, results) {
		console.log(err,results);
		});


}

/**
 * Push graph data from the server to the database, called after graph data has been received from a socket connection. The graph meta data is pushed to an sql database, and the graph document itself is pushed to a mongodb store, which provides fast acces as well as maintaining the data structure of the graphs (making it possible to query the internal graph data structure)
 * @param      	{Object}  the graph object
 * @param      	{Boolean} true/false representing weather the graph should be pushed to the public database
 * @return		{String} returns a uri to the resulting graph
 */
seedling.db_push_graph = function(fullgraph, public) {

    var url = "http://seeder.co/graph?graphid=" + fullgraph.graphid;
    
    /*
    	commit to mongodb, based on wether graph is public
    */
    

    if (public) {

        //console.log("database : connected to MongoDB");
        db.createCollection('publicgraphs', function(err, collection) {

            //console.log('insert with id ' + fullgraph.graphid);
            collection.update({
                graphid: fullgraph.graphid
            }, fullgraph, {
                upsert: true
            }, function(er, res) {
                console.log(res);
            });

        });

    } else {

        //console.log("database : connected to MongoDB");
        db.createCollection('usergraphs', function(err, collection) {

            //console.log('insert with id ' + fullgraph.graphid);
            collection.update({
                graphid: fullgraph.graphid
            }, fullgraph, {
                upsert: true
            }, function(er, res) {
                console.log(res);
            });

        });

    }
    
    /*
    	commit to sql database
    */
    
    console.log(+new Date(fullgraph.graphmeta.datecreated*1000).toString());
   
    var graph = 
    {
    			graphid : fullgraph.graphid, 
    			userid : fullgraph.author , 
    			graphname : fullgraph.graphname, 
    			description : fullgraph.graphdesc, 
    			nodecount : fullgraph.graphmeta.nsize, 
    			edgecount : fullgraph.graphmeta.esize, 
    			created : this.formatdate(new Date(fullgraph.graphmeta.datecreated)),
    			public : public
    }
     console.log(graph);
    var query = connection.query('INSERT INTO graphs SET ?;', graph, function(err, results) {
    	console.log(err,results);
    	});
    

    return url;

}

seedling.formatdate = function(date1) {
  return date1.getFullYear() + '-' +
    (date1.getMonth() < 9 ? '0' : '') + (date1.getMonth()+1) + '-' +
    (date1.getDate() < 10 ? '0' : '') + date1.getDate();
}


//utility function to grab from the mongodb database and copy graph metadata into the mysql db
// this should not be used routinely and is for special purposes at the inset of development.

seedling.syncDBS = function(){

		db.createCollection('publicgraphs', function (err, collection) {
		
		    collection.find().toArray(function (err, items) {
		        items.forEach(function (obj) {
		            var graph = 
		            {
		            			graphid : obj.graphid, 
		            			userid : 1 , 
		            			graphname : obj.graphname, 
		            			description : obj.graphdesc, 
		            			nodecount : obj.graphmeta.nsize, 
		            			edgecount : obj.graphmeta.esize, 
		            			created : '2014-08-13 00:00:00'
		            }
		            var query = connection.query('INSERT INTO graphs SET ?;', graph, function(err, results) {
		            	});
		        });
		    });
		});

}



seedling.verifyAuthPair = function(requsername, reqpass, next){
	
	//check if username exists
	
	connection.query('SELECT * FROM users WHERE username  = \'' + requsername + '\';', function(err, res){
			console.log(res[0]);
			if(res[0] ){
			//username exists
			//one result should be returned 
			
			//generate a hash from the submitted password + the saved guid
			hash = crypto.createHmac('sha512', hashkey);
			hash.update(reqpass+res[0].guid);
			var hashedpass = hash.digest('hex');
			
			console.log(res[0]);
			//console.log(hashedpass);
			
				if(hashedpass == res[0].password){
				//compare the hashed password to the stored hashed password and confirm or
				//refuse the connection
				next(res[0], 1);
				
				}else{
					next('AUTH :: Incorrect Username / Password', 0);
				}
				
			}else{
			next('AUTH :: Incorrect Username / Password', 0);
			}
			
	});

}



//seedling.registerUser(req.uname, req.fname, req.sname, req.guid, req.pass, req.email);
seedling.registerUser = function(regusername, regforename, regsurname, regpass, regemail, next){


connection.query('SELECT * FROM users WHERE username  = \'' + regusername + '\';', function(err, res){
		if(res[0]){
		
		//username exists already
		console.log(res);
		next('username already exists');
		
		}else{
							
							//user does not exist
							//generate a GUID
							var regguid = 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function(c) {
							    var r = Math.random()*16|0, v = c == 'x' ? r : (r&0x3|0x8);
							    return v.toString(16);
							});
					
					    //generate hash of passowrd and GUID
					    hash = crypto.createHmac('sha512', hashkey);
					    hash.update(regpass+regguid);
					    //keep hash in hex
					    var hashedpass = hash.digest('hex');
					    var user = 
					    {
					    	username : regusername,
					    	firstname : regforename,
					    	lastname : regsurname,
					    	email : regemail,
					    	password : hashedpass,
					    	guid : regguid,
					    	avatarurl : 'http://placehold.it/150x150.png',
					    	bio : 'tell us something about yourself',
					    	locationid : 1, 
					    	created : '2014-08-13 00:00:00',
					    	bannerurl : 'http://placehold.it/150x150.png'
					    }
					    
					   	//add the data to the users database
					    connection.query('INSERT INTO users SET ?;', user, function (err, results){ next(err); });
		
		
		}
});



	

}



